const cards = $input.first().json.cards || [];

const decodeHtml = (text) =>
  text
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'");

const extractRawHtmlByTestId = (html, key) => {
  const regex = new RegExp(
    `<div[^>]+data-testid=["']?${key}["']?[^>]*>([\\s\\S]*?)<\\/div>`,
    'i'
  );
  const match = html.match(regex);
  return match ? decodeHtml(match[1]) : null;
};

const extractCleanText = (html, key) => {
  const raw = extractRawHtmlByTestId(html, key);
  return raw ? raw.replace(/<[^>]+>/g, '').trim() : null;
};

const parseNumber = (text) => {
  if (!text) return null;
  const clean = text.replace(/[^\d,]/g, '').replace(/\./g, '').replace(',', '.');
  const num = parseFloat(clean);
  return isNaN(num) ? null : num;
};

const extractPointsByStrongTags = (html) => {
  if (!html) return [null, null];

  const matches = [...html.matchAll(/<strong>(.*?)<\/strong>/gi)];
  const first = matches[0]?.[1]?.trim() || null;
  const second = matches[1]?.[1]?.trim() || null;

  const pointsEarned = parseNumber(first);
  const pointsPerReal = parseNumber(second);

  return [pointsEarned, pointsPerReal];
};

// Mapeia os cards normalmente
const compras = cards.map((html, i) => {
  const accrualHtml = extractRawHtmlByTestId(html, 'ProductCard_AccrualInfos_DefaultAccrualText');
  const [pointsEarned, pointsPerReal] = extractPointsByStrongTags(accrualHtml);

  return {
    index: i + 1,
    title: extractCleanText(html, 'ProductCard_ProductTitle'),
    description: extractCleanText(html, 'Text_Typography'),
    clubPoints: parseNumber(extractCleanText(html, 'ProductCard_ProductPricingPts_ClubSalePrice')),
    defaultPoints: parseNumber(extractCleanText(html, 'ProductCard_ProductPricingPts_DefaultSalePrice')),
    brlPrice: parseNumber(extractCleanText(html, 'ProductCard_ProductPricingBrl_SalePrice')),
    pointsEarned,
    pointsPerReal,
  };
});

// Retorna um único item com a estrutura desejada
return [
  {
    json: {
      comprasDeHoje: compras
    }
  }
];

const tasks = $input.all().map(item => item.json);

// Extrai apenas os títulos e formata
const itemsDesejados = tasks.map(task => ({
  itemDesejado: task.title
}));

return [
  {
    json: {
      itemsDesejados
    }
  }
];

const allInputs = $input.all();

// Primeiro item tem `comprasDeHoje`
const comprasDeHoje = allInputs[0].json.comprasDeHoje;

// Segundo item tem `url`
const url = allInputs[1].json.url;

// Segundo item tem `itemsDesejados`
const itemsDesejados = allInputs[2].json.itemsDesejados;

const formatItemsDesejados = (items = []) => {
  return items.map((item, i) => `  ${i + 1}. ${item.itemDesejado}`).join('\n');
};

const formatComprasDeHoje = (compras = []) => {
  return compras.map((item, i) => {
    return `  ${i + 1}. ${item.title}
     - Descrição: ${item.description}
     - Preço (BRL): R$ ${item.brlPrice}
     - Pontos Club: ${item.clubPoints}
     - Pontos Cheios: ${item.defaultPoints}
     - Pontos ganhos: ${item.pointsEarned}
     - Pontos por real: ${item.pointsPerReal}`;
  }).join('\n\n');
};

const texto = `Url:\n${url}\n\nItens desejados:\n${formatItemsDesejados(itemsDesejados)}\n\nCompras de hoje:\n${formatComprasDeHoje(comprasDeHoje)}`;

return [
  {
    json: {
      texto
    }
  }
];

const compras = $input.first().json.comprasDeHoje;

const filtrado = compras.filter(item => item.pointsPerReal >= 5);

return [
  {
    json: {
      comprasDeHoje: filtrado
    }
  }
];
